INSERT INTO `broadcast_text_locale` VALUES (53929, 'enUS', 'The battle against Deathwing\'s minions is your top priority, $n. But if you can help our flight with this small task, so much the better.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53930, 'enUS', 'My jade!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53931, 'enUS', 'You have jade?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53932, 'enUS', 'The jade sings to us. Give! Give!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53933, 'enUS', 'Very well.', 'Very well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53934, 'enUS', 'Great!', 'Great!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53935, 'enUS', 'Shinies!', 'Shinies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53936, 'enUS', 'Very well.', 'Very well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53937, 'enUS', 'We is good.', 'We is good.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53938, 'enUS', 'Our patience is growing thin...', 'Our patience is growing thin...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53939, 'enUS', 'Good thing I don\'t mind waiting.', 'Good thing I don\'t mind waiting.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53940, 'enUS', 'We keep waiting then...', 'We keep waiting then...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53941, 'enUS', 'This is becoming unbearable.', 'This is becoming unbearable.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53942, 'enUS', 'We keep waiting.', 'We keep waiting.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53943, 'enUS', 'Those sneaky monkeys ambushed me out of nowhere.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53944, 'enUS', 'The Darkmoon Deathmatch is over!  $n is the victor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53945, 'enUS', '', 'I don\'t know that cub.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53946, 'enUS', '', 'That isn\'t Shin.  It looks super lifelike though.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53947, 'enUS', '', 'Eww.  That cub has a funny face.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53948, 'enUS', 'Are you friends with Admiral Taylor? If so, then I must test your strength in battle later.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53949, 'enUS', 'The water spirits have answered my prayers. Thank you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53950, 'enUS', 'I had given up hope, friend. Thank you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53951, 'enUS', 'Do my eyes deceive me? Am I truly free?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53952, 'enUS', 'I live another day, thanks to you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53953, 'enUS', 'Are you part of the rescue team? Are we saved?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53954, 'enUS', 'I knew someone would come for us. Thank you, $c!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53955, 'enUS', 'Thank you, $c. I can get back to camp on my own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53956, 'enUS', 'They\'ve got the Admiral locked up like an animal. Please save him too.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53957, 'enUS', 'The hozen revel in torturing their prisoners. Thank you, $c, for saving me from a dire fate.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53958, 'enUS', 'The undying flames are all that remain of this sacred place. I sense much anger here...a seething rage, barely held in check. Be on your guard.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53959, 'enUS', '', 'And you, with your heroic features. My husband\'s shrine needs guardians of your... stature.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53960, 'enUS', 'Come on now, mon!$b$bI be leakin\' like a sieve, here! I don\' be havin\' much time...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53961, 'enUS', 'Come with me, Zin\'Jun, let\'s find your missing... belongings.', 'Come with me, Zin\'Jun, let\'s find your missing... belongings.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53962, 'enUS', 'Be right with ya, mon...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53963, 'enUS', 'You\'ve almost got him! Og\'nor ka Lok\'tar - Now we finish this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53964, 'enUS', 'Aww, no... what have dey gone an\' done to me rifle, mon!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53965, 'enUS', 'Well lookie dere! Dat be my bad eye, but it be a good start!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53966, 'enUS', 'Dere it be, mon. My huntin\' eye! I\'ll be able ta rest in peace wit\' my ancestors...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53967, 'enUS', 'Wassat, mon? Dey kill ol\' Snuff?! Nooo!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53968, 'enUS', 'I haven\'t come to be stopped by the likes of you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53969, 'enUS', 'Dere we go, mon. Now I be goin\' off ta be wit Snuff...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53970, 'enUS', 'You have our thanks, $p. We\'ll be watchin\' over ya.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53971, 'enUS', '', 'What a waste.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53972, 'enUS', 'What is it you seek?', 'What is it you seek?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53973, 'enUS', 'Fight me!', 'Fight me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53977, 'enUS', 'Very well...', 'Very well...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53978, 'enUS', 'Let us celebrate!', 'Let us celebrate!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53979, 'enUS', 'The time for celebration is done.', 'The time for celebration is done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53980, 'enUS', 'Enough!', 'Enough!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53984, 'enUS', '<Rell\'s forehead is pale and beaded with sweat. He does not look good.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53985, 'enUS', '<Rell continues to stare blankly at the mat. His breathing is shallow and labored.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53986, 'enUS', 'Well done, Let us see to our friend...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53987, 'enUS', 'The rest of the drakes should be here shortly. I\'ll fly on ahead; catch up when you can.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53989, 'enUS', '', 'Rell, please... save your energy. Sully can tell $n what happened next.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53990, 'enUS', 'We had traveled north through the forest for several hours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53994, 'enUS', 'We stopped when we found a strange fallen tree.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53995, 'enUS', 'It wasn\'t supposed to go this way. We were supposed to find the White Pawn. I should be back in Ironforge by now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53996, 'enUS', 'Everything\'s gone to crap, $n. This was supposed to be a simple recon mission.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53997, 'enUS', 'I... <sniff>... I don\'t think I can handle the next part! Little Lu, tell $n what happened next.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53998, 'enUS', 'Alright, agents. Listen up.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (53999, 'enUS', 'Mmm... he considers me an agent.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54000, 'enUS', 'It grows late. We should set up camp for the night. Sully, do you have an axe?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54001, 'enUS', 'Naw, but I can put one together in a pinch. Let\'s see... you need a wood-cuttin\' axe, or a people-killin\' axe?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54002, 'enUS', 'Wood-cutting. Little Lu, use this axe to gather firewood.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54003, 'enUS', 'Yes sir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54004, 'enUS', 'Sully. Investigate those shrines to the west. Look for signs of the White Pawn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54005, 'enUS', 'Amber, find a good perch and give Sully some covering fire. We\'ll rendezvous back here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54006, 'enUS', 'Aye-aye, sir!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54007, 'enUS', '', 'Yes, sir.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54008, 'enUS', '', 'This way, Sully! I see the perfect spot.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54009, 'enUS', '', 'I\'ll hide up here in the shadows. If you run into any trouble, just shine a light on the target and I\'ll take care of them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54010, 'enUS', 'Nice shot, Amber!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54011, 'enUS', 'Didn\'t know I had a sniper on my side, did ya?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54012, 'enUS', 'I swear, that woman never misses.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54013, 'enUS', 'Note to self: Don\'t get on Amber\'s bad side.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54014, 'enUS', 'Boom! Nice one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54015, 'enUS', 'Tryin\' ta sneak up on ol\' Sully, huh?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54016, 'enUS', 'She hits \'em between the eyes, every time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54017, 'enUS', 'Yuck... splatter.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54018, 'enUS', 'I\'m in bad shape, $n. My injuries...I...I\'ll try to hang on a bit longer. Get me out of here, please.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54019, 'enUS', 'We were still goin\' north when we stopped near this big ol\' clearing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54020, 'enUS', 'We set up camp for the night. Rell split up responsibilities.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54021, 'enUS', 'I found an old rifle scope in my pockets. It\'d have to do.$b|TInterface\\Icons\\inv_misc_scopeb.blp:32|t$b', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54022, 'enUS', 'I readied my swords for battle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54023, 'enUS', 'If I saw anything dangerous, I\'d hit it with the light, and Amber\'s sniper rifle would take care of the rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54024, 'enUS', '<Shu looks at you expectantly.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54025, 'enUS', 'Shu, can you wake up Wuguo for me?', 'Shu, can you wake up Wuguo for me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54026, 'enUS', 'I found no sign of Anduin at the southwestern shrine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54027, 'enUS', 'However, I did find a friendly raccoon, who began to follow me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54028, 'enUS', 'I decided to name him Gizmo.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54029, 'enUS', 'NO! GIZMO!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54030, 'enUS', 'Food and drink taste even sweeter after a well fought battle.', 'Food and drink taste even sweeter after a well fought battle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54031, 'enUS', 'Hey there, little buddy! I\'m gonna name you Gizmo.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54032, 'enUS', 'AMBER, WHY!? I DIDN\'T EVEN SHINE MY DAMN LIGHT!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54033, 'enUS', 'Shortly afterward, another raccoon befriended me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54034, 'enUS', 'I named him Socks, and quickly moved on.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54035, 'enUS', 'Come on, Socks. Let\'s get out of here before you get sniped.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54036, 'enUS', 'Look there!  It looks as though the Twilight Cultists have brought the Old Gods and their corruption to Wyrmrest.  Hold on tight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54037, 'enUS', 'Grrrrr...*hic*...rrr...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54038, 'enUS', 'I found nothin\' at the northern shrine - no Anduin, no hozen... nothin\'.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54039, 'enUS', 'After scoutin\' the three shrines, I headed back over to camp.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54040, 'enUS', 'Socks followed along, faithfully.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54041, 'enUS', 'Pollen punch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54042, 'enUS', 'My orchids!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54043, 'enUS', 'Weeeeee!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54044, 'enUS', 'No... Rell...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54045, 'enUS', 'Nnn... nn nnnn... n-nl nnnr nnn... nn n nnnt...\n\n<I\'ll... be fine... it\'ll wear off... in a bit...>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54046, 'enUS', 'No. Your perversion of the dead ends here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54047, 'enUS', 'All these poor souls... here, this will lead you home.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54049, 'enUS', 'I WILL have you, spirit-sage!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54050, 'enUS', 'Augh! Too... much... It\'s too much!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54051, 'enUS', 'I cannot fall here. Not with so much work left to be done...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54052, 'enUS', 'Northrend\'s depths belong to the nerubians, $r.$B$BI have devoted myself to restoring that great truth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54055, 'enUS', '', 'Sir, we can have the entirety of the seventh fleet on the scene within the month--', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54056, 'enUS', 'Your Majesty! We\'ve finally restored Taylor\'s last transmission.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54057, 'enUS', 'Nightwind, have you pieced together that last transmission yet? Play me whatever you have!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54058, 'enUS', 'No time! We\'ll send a small team immediately. An elite force. Rescue the survivors, AND their precious cargo!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54059, 'enUS', 'Did you hear? They\'re safe somewhere!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54060, 'enUS', '...west of the planned rendezvous...Horde airfleet...[gunfire]...casualties!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54061, 'enUS', 'Victory is ours today!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54062, 'enUS', '...Thick fog bank to provide cover...retreat...uncharted land...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54063, 'enUS', '...hostile creatures. But the White Pawn is accounted for. Repeat: the White Pawn is safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54064, 'enUS', '...[Sound of swords clashing]...immediate danger! Requesting rescue, requesting --', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54065, 'enUS', '...encountered -- tribe -- extremely hostile creatures...we -- in immediate danger...Requesting rescue, I repeat--', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54066, 'enUS', 'Once the White Pawn is recovered, the full might of our navy can engage the Horde. On OUR terms.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54067, 'enUS', 'I returned to find Rell bein\' attacked by hozen! Again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54068, 'enUS', 'AMBER! AIM YER SIGHTS OVER HERE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54069, 'enUS', 'Things have changed rapidly since you arrived here, $n. The hozen are more of a threat now than ever before.$b$bBut we, jinyu... we are stronger now, too.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54070, 'enUS', 'We don\'t stand a chance if we face the hozen alone. We will need the help of the Alliance if our tribe is to survive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54071, 'enUS', 'Rell survived the attack, but he wasn\'t doing very well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54072, 'enUS', '', 'This is a very unusual place.$B$BThe magic I\'m channeling to try to repair the ley lines should be tearing my body apart, but I feel incredible!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54073, 'enUS', 'Don\'t throw banana peel where going to walk.', 'Don\'t throw banana peel where going to walk.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54074, 'enUS', 'Peel banana first, eat second.', 'Peel banana first, eat second.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54075, 'enUS', 'Steal a banana from a hozen, expect a angry hozen.', 'Steal a banana from a hozen, expect a angry hozen.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54076, 'enUS', 'Firecracker for throwing, banana for eating.', 'Firecracker for throwing, banana for eating.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54077, 'enUS', 'Wet fur not fun to sleep on.', 'Wet fur not fun to sleep on.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54078, 'enUS', 'Don\'t roll in own poo unless want to smell like poo all day.', 'Don\'t roll in own poo unless want to smell like poo all day.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54079, 'enUS', 'Don\'t pull own tail when there are other tails to pull.', 'Don\'t pull own tail when there are other tails to pull.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54080, 'enUS', 'Poo not good to eat, but very good to throw.', 'Poo not good to eat, but very good to throw.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54081, 'enUS', 'Thank you, friend. I\'d be bound to that wretched construct right now if it weren\'t for your intervention.$B$BStill, I shouldn\'t be here at all. We must put a stop to the spiritbinders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54082, 'enUS', 'I envy you in a way, little $r.$B$BYou are so fortunate as to bear witness to the first steps of the rebirth of the mighty Empire of Azjol-Nerub.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54083, 'enUS', 'The sign out front was not very... welcoming.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54084, 'enUS', 'The lawn was littered with small jade statues. They seemed almost... lifelike.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54085, 'enUS', 'One statue in particular stood out. I had never seen a creature like this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54086, 'enUS', 'Pshh! No sign is gonna stop me an\' my best pal Socks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54087, 'enUS', 'This person sure has lots o\' statues...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54088, 'enUS', 'Now wait a darn minute. Is that an orc? Is... is the HORDE here too!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54089, 'enUS', '', 'Now look at this... a jinyu and... what is that? Some sort of Beard-Sprite?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54090, 'enUS', 'I\'m a dwarf, ma\'am. Can we borrow some food?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54091, 'enUS', '', 'The Beard-Sprite will make an excellent addition to my family!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54092, 'enUS', 'What in blazes are you talkin\' about...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54093, 'enUS', 'Sully... those aren\'t statues on her lawn! She wants to turn you into jade!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54094, 'enUS', 'I finally understood what was going on... but I was a second too late.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54095, 'enUS', '', 'AHHH HA HA HAAAAA!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54096, 'enUS', 'SOCKS! NOOOOOOOOOOO!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54097, 'enUS', 'The Hour of Twilight falls - the end of all things - you cannot stop it. You are nothing. NOTHING!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54098, 'enUS', 'Grook them florkin\' wikkets!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54099, 'enUS', 'Grook \'em in the kroogs, boys!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54100, 'enUS', 'Grook \'em \'til they ook in their dookers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54101, 'enUS', 'Horg \'em off their jeeks and blikker \'em til their wik falls off!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54102, 'enUS', 'Grook \'em in the nook, then gork \'em clean!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54103, 'enUS', 'Can you close the portal, brother?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54104, 'enUS', 'Very well, we shall break it for you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54105, 'enUS', 'Let them come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54106, 'enUS', 'It is being maintained by the will of a powerful demon...Mannoroth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54107, 'enUS', 'I cannot break his will alone...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54108, 'enUS', '', 'He knows what we attempt. We have not much time, the forest crawls with his demons.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54109, 'enUS', 'There are so MANY of them...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54110, 'enUS', '', 'Mother moon, guide us through this darkness.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54111, 'enUS', 'Weak, pitiful creatures. Hardly worthy of being called a legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54112, 'enUS', 'Oh this will be fun...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54113, 'enUS', 'Wait, I have an idea.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54114, 'enUS', '', 'Illidan, what is in that vial? What are you doing?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54115, 'enUS', 'After inspecting the statues, we saw a female pandaren emerge from the house.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54116, 'enUS', 'What our people could not.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54117, 'enUS', 'Yes...YES. I can feel the raw power of the Well of Eternity! It is time to end this charade!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54118, 'enUS', 'Varo\'then, see that I am not disrupted by this rabble!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54119, 'enUS', 'Highguard, to arms! For your queen! For Azshara!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54120, 'enUS', '', 'I cannot strike them!  What is this demon magic?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54121, 'enUS', 'They are not where they appear to be!  Strike in an area, it is the only way to uncover the real one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54122, 'enUS', 'I see you\'ve arrived. \n$bThis is the eve of the sundering, when the collapse of the Well of Eternity fractured the continents of the world.\n$bHere, we will snatch up the Dragon Soul before it is lost to the mists of time.\n$bBut first, you must bring down the protective wards of Azshara\'s lackeys. You will find them within the palace. I will scout on ahead.\n$bGood luck, heroes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54123, 'enUS', 'Send me to portal 1.', 'Send me to portal 1.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54124, 'enUS', 'Send me to portal 2.', 'Send me to portal 2.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54125, 'enUS', 'Send me to portal 3.', 'Send me to portal 3.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54126, 'enUS', 'Send me to the boss.', 'Send me to the boss.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54127, 'enUS', 'Run, Little Lu! Run away!! Before she gets you too!', '', 26124);
