INSERT INTO `broadcast_text_locale` VALUES (54196, 'enUS', 'Hey $p! This way to Morning Breeze Village.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54197, 'enUS', 'It is crucial that we bring all of the three remaining spirits back safely. I am too old to do this myself, and for all their strengths, both Aysa and Ji are single-minded in their ideals.$B$BYou possess all the qualities of a hero, and a balance that they do not. It is you that must do this.', '', 26124);
