INSERT INTO `broadcast_text_locale` VALUES (54205, 'enUS', '', 'Greetings!  I am apprentice to Brewmaster Shadowpour, a pandaren we now call the Mad Brewer.$b$BHis reckless pursuit of flavor at the risk of safety has resulted in countless injuries, illnesses, and now even the wildlife of the area feels his mad brewing.  He is a villain, and I could use your help in stopping him.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54206, 'enUS', '', 'This is nothing at all like the Shimmering Flats.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54207, 'enUS', '', 'This is much more relaxing than being on the speedbarge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54208, 'enUS', '', 'I don\'t have to wory about getting sunburned out here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54209, 'enUS', '', 'Oh, that looks like fun!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54210, 'enUS', 'Look at this place!', 'Look at this place!', 26124);
