INSERT INTO `broadcast_text_locale` VALUES (54219, 'enUS', 'Everything seems to be in order here.', 'Everything seems to be in order here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54220, 'enUS', 'Sloppy, but adequate.  You seem capable in a fight and in need of direction.\n$bThere are hundreds of demons ahead, it would be suicide to go it alone:', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54221, 'enUS', 'Looks good so far.', 'Looks good so far.', 26124);
