INSERT INTO `broadcast_text_locale` VALUES (54223, 'enUS', 'Come Stormrage, and I will show you what happens to those that betray the lord of the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54224, 'enUS', 'The shadows will hide us well.\n$bI am concealed by this shadowcloak, and can imbue its magics onto you.\n$bIt will not make us invisible, but as long as we keep our distance from our foes we will remain hidden.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54225, 'enUS', 'For you, Azshara.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54226, 'enUS', 'Anything outside of that circle around you will not be able to see you, but the illusion will fade if you attack or take damage.\n$bI suggest you remain hidden until I say otherwise, these demons are too powerful to face head on and in full force.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54227, 'enUS', '', 'I will handle the demons. Elune, guide my arrows!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54228, 'enUS', 'Look at all these adorable animals!', 'Look at all these adorable animals!', 26124);
