INSERT INTO `broadcast_text_locale` VALUES (54230, 'enUS', 'Oh... So cute!', 'Oh... So cute!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54231, 'enUS', '', 'I will hold them back for now!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54232, 'enUS', '', '<Corastrasza steps between you and the nearby corpse, her face wrinkled with concern.>$B$BI\'m sorry, $n. This is a private matter for the red flight to resolve.$B$BPlease see if you can help the others outside.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54233, 'enUS', 'Tyrande is overwhelmed! Use the Blessing of Elune to drive the demons back!', 'Tyrande is overwhelmed! Use the Blessing of Elune to drive the demons back!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54234, 'enUS', 'If only I had more room in my store, I\'d take you all with me to Dalaran.', 'If only I had more room in my store, I\'d take you all with me to Dalaran.', 26124);
