INSERT INTO `broadcast_text_locale` VALUES (54239, 'enUS', 'Hmm...', 'Hmm...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54240, 'enUS', 'What happened here? What was stolen?', 'What happened here? What was stolen?', 26124);
