INSERT INTO `broadcast_text_locale` VALUES (54251, 'enUS', 'Illidan has been knocked to the ground!  Mannoroth is looking for a new target!', 'Illidan has been knocked to the ground!  Mannoroth is looking for a new target!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54252, 'enUS', 'Illidan has recovered!', 'Illidan has recovered!', 26124);
