INSERT INTO `broadcast_text_locale` VALUES (54254, 'enUS', 'The sword has pierced his infernal armor! Strike him down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54255, 'enUS', 'Varo\'then\'s magical sword falls to the ground!', 'Varo\'then\'s magical sword falls to the ground!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54256, 'enUS', 'Lord Sargeras, I will not fail you! Sweep your molten fist through this world, so that it may be reborn in flames and darkness!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54257, 'enUS', 'He is still connected to the Well somehow! Focus your attacks on Mannoroth, we must disrupt his concentration!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54258, 'enUS', '', 'Illidan, I am out of arrows! Moon goddess, protect us from the darkness, that we may see your light again another night!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54259, 'enUS', 'Yes...yes! I can feel his burning eyes upon me, he is close...so close! And then your world will be unmade, your lives as nothing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54260, 'enUS', 'A massive demonic portal opens nearby!', 'A massive demonic portal opens nearby!', 26124);
