INSERT INTO `broadcast_text_locale` VALUES (54263, 'enUS', 'Tyrande collapses!  The Light of Elune winks out!', 'Tyrande collapses!  The Light of Elune winks out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (54264, 'enUS', 'I will be the savior of our people! I WILL FULFILL MY DESTINY!', '', 26124);
