INSERT INTO `broadcast_text_locale` VALUES (55071, 'enUS', 'Almost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55072, 'enUS', '', 'How about now?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55073, 'enUS', '', 'Come ON, Uncle Chen! What are we waiting for?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55074, 'enUS', 'Relax, Li Li. We have all the time in the world to explore Pandaria. Take some time to enjoy the trip.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55075, 'enUS', '', 'I would ENJOY the trip more if we were actually GOING somewhere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55076, 'enUS', 'You and all your friends are welcome to stay here for as long as you like.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55077, 'enUS', 'Does all of this farmland belong to you?', 'Does all of this farmland belong to you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55078, 'enUS', 'Hey! Got out of here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55079, 'enUS', 'Stay away from my sheep!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55080, 'enUS', 'Come back here, you coward!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55081, 'enUS', 'Bring back my sheep!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55082, 'enUS', 'Get back over here, thief!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55083, 'enUS', 'Despair...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55084, 'enUS', 'You will not take our waters!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55085, 'enUS', 'A worthy sacrifice for our master.', '', 26124);
