INSERT INTO `broadcast_text_locale` VALUES (55087, 'enUS', 'The corruption will consume you, as it consumed us...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55088, 'enUS', 'The waters are forbidden to all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55089, 'enUS', 'In the end, only silence remains.', '', 26124);
