INSERT INTO `broadcast_text_locale` VALUES (55095, 'enUS', 'First kill turnips! Then kill pandaren that grows them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55096, 'enUS', 'We murder every turnip!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55097, 'enUS', 'Turnips! Why you so bad?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55098, 'enUS', 'Turnips must be punished!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55099, 'enUS', 'Pandaren pay for growing disgusting turnips!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55100, 'enUS', 'Be at peace.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55101, 'enUS', 'Be cleansed, spirit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55102, 'enUS', 'Shadows, begone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55103, 'enUS', 'Be at peace, spirit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55104, 'enUS', 'I am finally...free...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55105, 'enUS', 'My boat winning!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55106, 'enUS', 'The light...returns...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (55107, 'enUS', 'I think my boat stuck.', '', 26124);
