INSERT INTO `broadcast_text_locale` VALUES (56077, 'enUS', 'Heroes, we must place this burden on your shoulders once again.  You must protect us from Deathwing\'s forces while we imbue the Soul with the powers of the great Aspects of Azeroth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56078, 'enUS', 'You may begin your ritual, we will defend you.', 'You may begin your ritual, we will defend you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56079, 'enUS', 'Our engines are damaged!  We\'re sitting ducks up here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56080, 'enUS', 'It is good to see you again, Alexstrasza. I have been busy in my absence.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56081, 'enUS', '', 'They... are my clutch no longer.  Bring them down.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56082, 'enUS', 'This library holds thousands of years of history and lore. The greatest knowledge in all of Pandaria is kept here.$B$BThat is why we must keep this great treasure safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56083, 'enUS', 'The scrolls! I must save the scrolls!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56084, 'enUS', 'It is no use, the power of the Soul is too great. I cannot wield it safely; the raging forces within it may be the doom of us all.', '', 26124);
