INSERT INTO `broadcast_text_locale` VALUES (56095, 'enUS', '', 'You\'ve been a really big help here at the Temple!$B$BI heard the Elder Sage say that he\'s glad you arrived when you did.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56096, 'enUS', '', 'Welcome to the Temple of the Jade Serpent, stranger. Our doors are open to all who seek knowledge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56097, 'enUS', 'Teleport to the garden entrance.', 'Teleport to the garden entrance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56098, 'enUS', '', 'Why is all of your grain so bland?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56099, 'enUS', '', 'They have failed us sister.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56100, 'enUS', '', 'I have awakened only to sleep once again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56101, 'enUS', 'The grain we gather needs to be in large quantities for the brewery. But I may know of a grain that would suit your needs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56102, 'enUS', 'It was a short time, for our part...', '', 26124);
