INSERT INTO `broadcast_text_locale` VALUES (56104, 'enUS', 'There is perhaps a way...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56105, 'enUS', 'Li Li... let\'s go. Your uncle has got something to prove.', '', 26124);
