INSERT INTO `broadcast_text_locale` VALUES (56119, 'enUS', 'Oh hello! Haven\'t seen you around before...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56120, 'enUS', '%s snaps its tether with a mighty effort, breaking free!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56121, 'enUS', 'You can find your malts at the granary, I reckon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56122, 'enUS', '', 'Mudmug!', 26124);
