INSERT INTO `broadcast_text_locale` VALUES (56124, 'enUS', 'I\'m here to help with that fancy beer you was talkin\' about.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56125, 'enUS', 'Glad to have you. We will be making a stout. Can you get a few barrels of water?', '', 26124);
