INSERT INTO `broadcast_text_locale` VALUES (56988, 'enUS', '', 'I\'m gonna run.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56989, 'enUS', 'Thank you for bringing back Yi-Mo.$B$BIt\'s clear to me now that we can no longer ignore our duties in the province.', 'Thank you for bringing back Yi-Mo.$B$BIt\'s clear to me now that we can no longer ignore our duties in the province.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (56990, 'enUS', 'Normally we\'d have plenty on hand, but most of our supplies have been left out to ruin in the rain.$B$BHowever, I\'m certain the creatures in the dried up ponds are a mixture of rainwater and salt from the earth. You may be able to get what you need from them.', 'Normally we\'d have plenty on hand, but most of our supplies have been left out to ruin in the rain.$B$BHowever, I\'m certain the creatures in the dried up ponds are a mixture of rainwater and salt from the earth. You may be able to get what you need from them.', 26124);
