INSERT INTO `broadcast_text_locale` VALUES (57135, 'enUS', 'Today, $n, we celebrate your graduation. You have learned all that there is to know within the walls of Tian.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57136, 'enUS', 'Welcome back, $n! How goes your training?$b$bOur trainees have missed you dearly... though I must say that they appreciate not having to spar with you anymore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57137, 'enUS', 'There is no hope, no future. All life is doomed.', '', 26124);
