INSERT INTO `broadcast_text_locale` VALUES (57153, 'enUS', '%s\'s gut churns violently.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57154, 'enUS', 'Are you sure you want to pay?', 'Are you sure you want to pay?', 26124);
