INSERT INTO `broadcast_text_locale` VALUES (57161, 'enUS', 'Faster, you fools. We need all of these artifacts soon or it\'s the cane for your hides.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57162, 'enUS', 'Nobody can be sad when they wearin\' a mask like this! Now, who can we test it on?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57163, 'enUS', 'Perfect! C\'mere, buddy. Sorry about last time! Put this on.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57164, 'enUS', 'Ugh... what was that stuff? It went through me like bad tea.', 'Ugh... what was that stuff? It went through me like bad tea.', 26124);
