INSERT INTO `broadcast_text_locale` VALUES (57245, 'enUS', '', 'We\'re almost done. Just the homestretch now!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57246, 'enUS', '', 'Well done, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57247, 'enUS', '', 'You fell too far behind!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57248, 'enUS', '', 'You must keep up with me, $n. Venture too far and you will fail the exercise.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57249, 'enUS', '', 'Speak to me when you are ready to try again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57250, 'enUS', '', 'You must fly through all 10 checkpoints before you can finish the race.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57251, 'enUS', '', 'In a real race, you will need to fly through each checkered flag to get credit for passing the checkpoint.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57252, 'enUS', '', 'You are too far! Get closer to your Instructor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57253, 'enUS', 'Oh... what... who? How did I get here?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57254, 'enUS', 'Sorry I made such a mess o\' things.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57255, 'enUS', 'I love ya pop.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57256, 'enUS', 'Junior! Junior! Snap out of it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57257, 'enUS', 'Junior! Junior! Snap out of it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57258, 'enUS', 'Come on boy, you\'re stronger \'an this thing.', '', 26124);
