INSERT INTO `broadcast_text_locale` VALUES (57262, 'enUS', '<Try shaking Hemet.>', '<Try shaking Hemet.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57263, 'enUS', '$p, watch the corners. I hear something comin\'.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57264, 'enUS', 'You chose the wrong hunter to hunt! $p, kite that thing a little further away so I can get a better shot.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57265, 'enUS', 'We did it, $p! That was some good butt-punchin\' back there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57266, 'enUS', '$p... To think you\'d risked your life to save me - twice now - and I\'d only treated you with disdain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57267, 'enUS', 'I am shamed beyond comprehension. Please forgive me - my mind was not my own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57268, 'enUS', 'Should you ever need aid of any kind, from a handful of turnip seeds to a volley of arrows, know that Zhu\'s Watch will provide it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57269, 'enUS', 'For now, I must direct my attention to rebuilding the village. There is much to be done. You may rest here if you like, but we\'ll not accept any more help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57270, 'enUS', 'Something tells Ken-Ken that\'s not the last of the sads, though...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57271, 'enUS', 'Where are we? Smells like... like carrots and turds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57272, 'enUS', 'Who\'s that guy? The guy you were talkin\' to? Can someone tell me what\'s goin\' on?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57273, 'enUS', 'Where you takin\' me, $p?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (57274, 'enUS', 'Was that a hozen I heared? Are we in Halfhill yet?', '', 26124);
