INSERT INTO `broadcast_text_locale` VALUES (58818, 'enUS', 'You are a good friend, $n. Maybe my only friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58819, 'enUS', 'Hey $Gbrother:sister;! You come to ask Jogu for wishdom?', '', 26124);
