INSERT INTO `broadcast_text_locale` VALUES (58846, 'enUS', '', 'I don\'t recognize your face. You must be new in town.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58847, 'enUS', '', 'I know just about everyone in these parts. I\'ll let them know there\'s a new face in town.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58848, 'enUS', '', 'Oh, you\'re not from around here. Are you a citypaw? Tell me, how do you like my dress?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58849, 'enUS', '', 'Hi there. We\'ve met before, haven\'t we?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58850, 'enUS', '', 'Oh, what a nice outfit you have there. I hadn\'t noticed before.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58851, 'enUS', '', 'Folks \'round here have been talking about you. Don\'t worry, I put in a good word now and then.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58852, 'enUS', '', 'Do you like this color on me?', 26124);
