INSERT INTO `broadcast_text_locale` VALUES (58856, 'enUS', '', 'Hi buddy! Please tell me... do you like my earrings?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58857, 'enUS', '', 'Hello, friend. Are you getting on all right at your farm?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58858, 'enUS', '', 'Now that we are friends, I must tell you that your outfit is... hideous.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58859, 'enUS', '', 'Folks \'round here have been talking about you, friend! They do seem to fancy you quite a lot.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58860, 'enUS', '', 'Please, as a favor to a friend... tell me the truth about my outfit. Is it... awful?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58861, 'enUS', '', 'Now that we\'re friends, I have a confession to make. I... I think I quite fancy Farmer Fung.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58862, 'enUS', '', 'Don\'t you think that Farmer Fung is just... dreamy?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58863, 'enUS', '', '$n, my good friend! I hope everything is going well for you and Mr. Yoon at the farm.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58864, 'enUS', '', 'Now that we are good friends, I must tell you that your outfit is... atrocious.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58865, 'enUS', '', 'Folks \'round here have been talking about you, $n! You\'re making quite a stir around here.', 26124);
