INSERT INTO `broadcast_text_locale` VALUES (58871, 'enUS', '', 'Please, as a favor to your best friend... tell me the truth about my outfit. How is it?$B$BOh? Wonderful you say? Why thank you! This is why you\'re my friend for life!', 26124);
