INSERT INTO `broadcast_text_locale` VALUES (58873, 'enUS', 'Thank you!', 'Thank you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58874, 'enUS', 'Quick, let\'s get out of here.', 'Quick, let\'s get out of here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58875, 'enUS', 'Death to the yaungol!', 'Death to the yaungol!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58876, 'enUS', 'Our hero!', 'Our hero!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58877, 'enUS', 'We\'re saved!', 'We\'re saved!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58878, 'enUS', 'Let\'s go to Binan Village.', 'Let\'s go to Binan Village.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58879, 'enUS', 'Good riddance.', 'Good riddance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58880, 'enUS', 'Hey lookee, it\'s the new $Gguy:gal; in town.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58881, 'enUS', 'You know anything \'bout yaks, rookie? Well I know a lot about \'em.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58882, 'enUS', 'You know who\'s got the biggest vegetables in the Valley? You\'re lookin\' at him!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58883, 'enUS', 'Hey buddy, what\'s happening?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58884, 'enUS', 'Hey buddy, feel free to come admire my yaks anytime.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58885, 'enUS', 'Hey $r, buddy to buddy, I\'ll let you in on a secret. You want big vegetables? You gotta use fertilizer.$B$BAnd you know who\'s got the best fertilizer in the Valley? Me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58886, 'enUS', 'Hey friend, what\'s happening?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58887, 'enUS', 'Hey friend, feel free to come admire my yaks anytime.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58888, 'enUS', 'Hey friend, here\'s a tip. You want big vegetables? You gotta use fertilizer.$B$BAnd you know who\'s got the best fertilizer in the Valley? Me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58889, 'enUS', 'My good friend! How are things at the farm?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58890, 'enUS', 'My good friend, how are you today?$B$BListen, I don\'t say this to just anyone, but if you ever need a yak, you let Fung know.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58891, 'enUS', 'My good friend, here\'s a tip. You want big vegetables?$B$BYou gotta tend them. Give them some water, some fertilizer, and some love.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58892, 'enUS', 'Hey neighbor! Things going well at the farm?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58893, 'enUS', 'Hey neighbor, how are you today?$B$BHow\'s Shaggy doing?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58894, 'enUS', 'Hey neighbor! Those vegetables are looking good.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58895, 'enUS', '', 'Hey there! Have I seen yer face before?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58896, 'enUS', '', 'Hey there! Ain\'t seen a face like yours \'round here before.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58897, 'enUS', '', 'Hey there, citypaw! You heard o\' any good stories that you can share? I love tellin\' stories!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58898, 'enUS', '', 'Hey, have you heard the story \'bout Old Man Thistle? That\'s a good story if I ever heard \'un.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58899, 'enUS', '', 'Hey, why\'s your name $n? Isn\'t that a weird name?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58900, 'enUS', '', 'Folk \'round here are mighty good... if you stick \'round long enough to find out.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58901, 'enUS', '', 'My papa always told me that fish swim \'n birds fly. Now if I can swim AND fly, what does that make me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58902, 'enUS', '', 'Hey buddy! Mighty nice seein\' you again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58903, 'enUS', '', 'Hey there, buddy! I loved that story \'bout that boy Arthas. You got any others to share?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58904, 'enUS', '', 'Hey there, buddy! I loved that story \'bout that orc Thrall. You got any others to share?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58905, 'enUS', '', 'Old Man Thistle owned a little plot o\' land just o\'er there on that hill.$B$BNow one day he was diggin\' and diggin\', and he dug so far down he didn\'t do but found a whole cave underneath these them hills!$B$BAnd what do you suppose he found in that cave?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58906, 'enUS', '', 'Whaddaya mean Fish is a weird name, too? It ain\'t weird at all, buddy! I was named after dem fishes in the lake!$B$BNow just now what\'s a \"$n,\" eh? I ask ya that!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58907, 'enUS', '', 'My papa always told me to make friends \'stead o\' enemies. That\'s why ye\'re my buddy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58908, 'enUS', '', 'Hey friend! Mighty nice seein\' you again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58909, 'enUS', '', 'Hey there, friend! Tell me again that story \'bout the world tree and the... whatcha callit... the sunderin\'!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58910, 'enUS', 'You found...', 'You found...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58911, 'enUS', '', 'Old Man Thistle found a cave full o\' TREASURE!!!!$B$BThere was gold \'n diamonds \'n jade \'n statues \'n a whole room full o\' books!$B$BWell he wandered \'round that cave \'n before long a whole day had passed \'n he was gettin\' mighty hungry.$B$BSo he heads back toward the way he came and what happened but he couldn\'t find no way back out agin\'!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58912, 'enUS', 'A Lost Hen! Bring her back to a chicken coop.', 'A Lost Hen! Bring her back to a chicken coop.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58913, 'enUS', 'An Amorous Rooster! Dip him in the water to cool his passions.', 'An Amorous Rooster! Dip him in the water to cool his passions.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58914, 'enUS', 'A Wild Prairie Chicken! Kick him out of here!', 'A Wild Prairie Chicken! Kick him out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58915, 'enUS', 'A Marmot Disguised in Chicken Feathers! Expose him for the sham he really is!', 'A Marmot Disguised in Chicken Feathers! Expose him for the sham he really is!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58916, 'enUS', 'Old Hillpaw\'s prize-winning chicken, Mother Clucks!', 'Old Hillpaw\'s prize-winning chicken, Mother Clucks!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58917, 'enUS', '', 'Now that we\'re friends, you can call me Fish.$B$BHa ha! I\'m so funny!$B$BI\'m jus\' kidding... everyone calls me Fish! That\'s cuz everyone\'s my friend!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58918, 'enUS', 'Old Hillpaw\'s prize-winning chicken, Madam Specklybottom!', 'Old Hillpaw\'s prize-winning chicken, Madam Specklybottom!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58919, 'enUS', 'Old Hillpaw\'s prize-winning chicken, Thunderpeck!', 'Old Hillpaw\'s prize-winning chicken, Thunderpeck!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58920, 'enUS', 'Old Hillpaw\'s prize-winning chicken, Mantidsbane!', 'Old Hillpaw\'s prize-winning chicken, Mantidsbane!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58921, 'enUS', '', 'My papa always told me that friends are worth more than sweet turnips. That\'s why yer my friend, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58922, 'enUS', 'Old Hillpaw\'s prize-winning chicken, Rumblefeather!', 'Old Hillpaw\'s prize-winning chicken, Rumblefeather!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58923, 'enUS', 'Old Hillpaw\'s prize-winning chicken, Egga!', 'Old Hillpaw\'s prize-winning chicken, Egga!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58924, 'enUS', '', 'Howdy, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58925, 'enUS', 'Old Hillpaw\'s prize-winning chicken, Stumbleflutter!', 'Old Hillpaw\'s prize-winning chicken, Stumbleflutter!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58926, 'enUS', 'Old Hillpaw\'s prize-winning chicken, Buck-Bucka Betty!', 'Old Hillpaw\'s prize-winning chicken, Buck-Bucka Betty!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58927, 'enUS', '', 'Hey there, my good friend! Tell me again that story \'bout that scary dragon, Deathwing!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58928, 'enUS', '', 'So Old Man Thistle\'s stuck down the cave, so what does he do but whistle fer his trusty mushan, Bobo!$B$BAnd right fore long, he hears a boomin\' noise, and Bobo busts a hole right in that cave!$B$BThat mushan done hear his master and come fer him!$B$BAnd then, what happens but the whole cave starts a shakin\' and the ceiling starts crashing down.$B$BSo Old Man Thistle jumps on Bobo\'s back and you know what happened\' then?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58929, 'enUS', '', 'Yeh know my good friend, I seen a lot o\' funny names in my time. Fish really ain\'t that strange. No, it\'s downright normal!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58930, 'enUS', '', 'My papa always told me that good friends are worth more than a chipper carrot. That\'s why yer my good friend, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58931, 'enUS', '', 'Howdy, neighbor! Whatcha doin\' today?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58932, 'enUS', '', 'Hey there, neighbor! I love stories so much, I just wanna hear \'em all!$B$BBut ya know, I think we\'ve made some good stories together too, dontcha think?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58933, 'enUS', '', 'So the cave\'s a crashing down, and Old Man Thistle and Bobo run as fast as they can! They twist and turn through the caves, til they burst outta the ground right up that hill!$B$BAnd what happened then but the cave closed up right behind \'em, like it got swallowed by the earth.$B$BOld Man Thistle\'s shaking, but first thing he does when he gets home is make a map to that cave full o\' treasure. Cuz maybe it\'s still there, true?$B$BGood thing he did, cuz the next day, he couldn\'t remember as well, and the day after that he remembered even less, till he plumb forgot \'bout that cave altogether.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58934, 'enUS', '', 'Hey neighbor, what\'re we talkin\' bout anyway? Ain\'t nothing in a name!$B$BLike my papa always said, ye\'d still eat a pink melon if it were called an onion, right?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58935, 'enUS', '', 'My papa always told me that neighbors are worth more than earth. Now that\'s sayin\' something!$B$BThat\'s why yer my neighbor, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58936, 'enUS', '', 'I take care o\' the pigs \'round here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58937, 'enUS', '', 'Hey buddy! The pigs are doin\' jus\' fine today!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58938, 'enUS', '', 'Howdy, friend! You gone down and seen my pigs yet?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58939, 'enUS', '', 'Howdy, my good friend! Now why my mama and papa didn\'t name me Pig, I jus\' don\'t know.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58940, 'enUS', '', 'Hey neighbor! How\'re those pigs doin\' at yer farm? I bet they\'re mighty happy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58941, 'enUS', 'You new here?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58942, 'enUS', 'Hey, Chee Chee real busy. You got some-ting you want?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58943, 'enUS', 'Chee Chee\'s sheep sheep are so scared... bad monkeys on hill! Bad monkey!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58944, 'enUS', 'Yo, I know you before. You help me, yes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58945, 'enUS', 'Chee Chee got big problem. Big, big problems.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58946, 'enUS', 'Chee Chee work hard for farm. Now bad monkeys come take away!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58947, 'enUS', 'Hey. You are Chee Chee\'s buddy, yes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58948, 'enUS', 'Chee Chee got big problem. Big, big problems. Buddy help Chee Chee?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58949, 'enUS', 'Chee Chee work hard for farm. Buddy understand, right?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58950, 'enUS', 'Hey friend. I got problems, but no need to be your problems, too.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58951, 'enUS', 'Hey friend, how is things going on your farm?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58952, 'enUS', 'Chee Chee is only hozen with own farm!$B$B<Chee Chee thumps his chest proudly.>$B$BYou, friend, come visit me at my farm sometime, okay?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58953, 'enUS', 'My good friend! You drink with Chee Chee sometime, yes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58954, 'enUS', 'Good friend, is things going good for you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58955, 'enUS', 'Chee Chee raise only sheep in the fields!$B$BAnyone want sheep, they come to Chee Chee.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58956, 'enUS', 'Hey neighbor! You like the sheep?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58957, 'enUS', 'Neighbor, because of you, my problems is less somewhat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58958, 'enUS', 'I give only nice sheep for you, neighbor! What do you think?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58959, 'enUS', '', 'Oh! Hi! Um... I should probably... go...$B$B<Ella blushes as her voice lowers to a whisper.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58960, 'enUS', '', 'Do you like... turnips?$B$B<Ella turns pink and shakes her head.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58961, 'enUS', '', 'Do you like kitties? I love them!$B$B<Ella looks surprised at her sudden outburst and smiles shyly.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58962, 'enUS', '', 'Hey there, $n. How are you today?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58963, 'enUS', '', 'I grow turnips at my family farm. Oh... I don\'t know if you wanted to know that...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58964, 'enUS', '', 'I don\'t have that many cats, right? I mean, it\'s quite reasonable when you think about it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58965, 'enUS', '', 'Hey there, friend! Isn\'t it a lovely day?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58966, 'enUS', '', 'Did you know that turnips make quite a tasty brew?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58967, 'enUS', '', 'The cats keep me company. It\'s been quite lonesome on the farm since my parents passed...$B$BOh no, I didn\'t mean to tell you so much.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58968, 'enUS', '', 'Hello, good friend. Oh, is it okay if I call you my good friend?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58969, 'enUS', '', 'I\'ve been experimenting with all sorts of brews in my spare time.$B$BWith the Stormstout brewery right across the way, I can\'t help but get inspired sometimes.$B$BOh, but these aren\'t anywhere as good as what they make over there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58970, 'enUS', '', 'How many cats do I have?$B$BOh, ummm... I think I may have lost track...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58971, 'enUS', '', 'Howdy, neighbor! The turnips are looking mighty fine this morning.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58972, 'enUS', '', 'So I\'ve been sharing some of my brews around town, and everyone is raving! I can hardly believe it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58973, 'enUS', '', 'How is Luna doing? She\'s such a sweet cat!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58974, 'enUS', 'The convoy to Kota Basecamp is leaving now! Gather around!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58975, 'enUS', 'Got trouble in the fields. Whether it\'s virmen or hozen, I tell ya, it ain\'t good.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58976, 'enUS', 'Yeah, I am the head of the Tiller\'s Union. Why do you ask?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58977, 'enUS', 'Sure the fields here are prosperous, but that\'s on account of hard, back-breaking work!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58978, 'enUS', 'Hey buddy, you got any experience dealing with hozen or virmen?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58979, 'enUS', 'Buddy, did you know that every farmer worth his dirt is a member of the Tiller\'s Union?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58980, 'enUS', 'Hey, if it isn\'t my buddy $n! I\'ve just come from a long day in the fields.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58981, 'enUS', 'Hey friend, you got any experience dealing with hozen or virmen?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58982, 'enUS', 'Hey friend, how\'s the soil looking today?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58983, 'enUS', 'My friend, $n! Let us share a drink together.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58984, 'enUS', 'My good friend, you must know a thing or two about exterminating virmen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58985, 'enUS', 'You know, good friends always share their farming secrets. Now tell me, how do you get your vegetables looking like that?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58986, 'enUS', 'A long day in the fields makes a man thirsty, you know what I mean, good friend?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58987, 'enUS', 'Hey neighbor, how is that mushan beast working out for you? She\'s quite a beauty, ain\'t she?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58988, 'enUS', 'Now that we\'re neighbors, you have to tell me your farming secrets. Do you use fertilizer? Growth spray? Weedkiller? Tell me, already!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58989, 'enUS', 'Howdy, neighbor. Would you like a cold brew?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58990, 'enUS', 'Thank you, traveler. I think I can make it now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58991, 'enUS', 'I ain\'t got time for no strangers. Now please leave me be.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58992, 'enUS', 'Can\'t you see I\'m busy? Got things I\'m working at, stranger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58993, 'enUS', 'YOU HAVE WON THE SCENARIO. HOORAY!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58994, 'enUS', 'Bah! Leave an old man be!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58995, 'enUS', 'You have my gratitude. I should return back to training now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58996, 'enUS', 'Humph. I\'m busy, can\'t you see?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58997, 'enUS', 'Do ye pester everyone this much? Sheesh.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58998, 'enUS', 'Pah! I know I seen you around, but that don\'t make us friends. Go away!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (58999, 'enUS', 'A simple flesh wound won\'t keep me from the battle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59000, 'enUS', 'Hey buddy, I\'m busy here. Can\'t ye come back later?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59001, 'enUS', 'Buddy, ye sure got a big mouth. Does it ever stop yappin\'?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59002, 'enUS', 'Buddy, ye know... sometimes it\'s nice to see you around.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59003, 'enUS', 'Hey friend, I\'m a little busy here. Ye got something ya need?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59004, 'enUS', 'Friend, you sure do run your mouth when you get a chance. But it\'s kinda nice to hear somethin\' other than silence now and again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59005, 'enUS', 'I got three sick chickens and another two hens not layin\'. I got too many problems to listen to yours, citypaw.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59006, 'enUS', 'Hey buddy. I got two chickens gone missin\' last night. Bet it was them hozen that\'re behind it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59007, 'enUS', 'Howdy, friend. You ever tried a fresh egg from one o\' my chickens? Dee-licious, I tell ya.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59008, 'enUS', 'Hey friend, thanks for visiting today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59009, 'enUS', 'Hey, it\'s my good friend $n! Anything I can do fer ya?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59010, 'enUS', 'My good friend, you got any news for me today?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59011, 'enUS', 'Ye know, $n, I gotta admit I look forward to your visits now and again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59012, 'enUS', 'Hey $n, I been thinkin\' that your farm needs some chickens. What do you think?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59013, 'enUS', 'Howdy, neighbor. Nice day today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59014, 'enUS', 'Hey, neighbor! Fields are lookin\' like a shiny new copper today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59015, 'enUS', 'Howdy, neighbor! Good to see ya! You come visit me any time, you hear?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59016, 'enUS', 'Well, $n? How are those chickens doing?$B$BMake sure to look around for their eggs. Those darned things hide them in the weirdest places!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59017, 'enUS', '', 'Quiet, stranger. I need complete focus for the task at hand.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59018, 'enUS', '', 'I have no quarrel with you, stranger.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59019, 'enUS', '', 'Only in silence will we hear the trees speaking.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59020, 'enUS', '', 'Quiet, $r. Please do not break my concentration.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59021, 'enUS', '', 'I have seen you in these parts before. I can see that you are a $c, from the look of you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59022, 'enUS', '', 'One gains little wisdom from speaking. One gains much wisdom from listening.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59023, 'enUS', '', 'Hey buddy, you are making enough noise to scare off a pack of mushan beasts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59024, 'enUS', '', 'Do not tell me what it is like to be a $c. Show me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59025, 'enUS', '', 'I am studying the hozen\'s movements. When I find their weakness, I will strike.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59026, 'enUS', '', 'My friend, you are making enough noise to scare off a pack of mushan beasts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59027, 'enUS', '', 'My friend, one of these days we should see who is stronger in battle - you, or me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59028, 'enUS', '', 'A wise man does not rush into war. A wise man wins a war.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59029, 'enUS', '', 'My good friend, you are making enough noise to scare off a pack of mushan beasts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59030, 'enUS', '', 'My good friend, one of these days we should see who is stronger in battle - you, or me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59031, 'enUS', '', 'In my home town, the greatest gift one could give to a good friend was a tree.$B$BDo you know why?$B$BA tree represents peace, growth, and life. It is a blessing of friendship.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59032, 'enUS', '', 'Neighbor, you move quietly today.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59033, 'enUS', '', 'Neighbor, I would gladly fight alongside you any day.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59034, 'enUS', '', 'Do you like your tree? I picked the sapling myself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59035, 'enUS', '', 'Hey friend, you know the secret to a good market? Distribution. You need a way to move your goods to the people who want \'em.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59036, 'enUS', '', 'You know what I been thinking, my good friend? You need a way to keep up with all your friends at home.$B$BI know a carpenter who can come by an install your own mailbox. How\'s that for an idea?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59037, 'enUS', '', 'How\'s that mailbox workin\' for ya? Good idea, right?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59038, 'enUS', '', 'Hey friend, don\'t you think that Farmer Yoon\'s house is looking mighty empty?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59039, 'enUS', '', 'I know a thing or two about decorating, my good friend. I know just what Farmer Yoon needs for his house!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59040, 'enUS', '', 'Hey neighbor! How do you like the new furnishings in Farmer Yoon\'s house? Yes, I picked them out myself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59041, 'enUS', 'Chee Chee love to eat Valley Stir Fry. You find any, you bring to me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59042, 'enUS', '', 'Any assistance getting the wounded back on their feet would be appreciated.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59043, 'enUS', 'Chee Chee like to look at sky. So pretty... so blue...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59044, 'enUS', '', 'Oh, gifts? I hope you\'re not thinking of getting me anything!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59045, 'enUS', '', 'Well you know me... I like cats!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59046, 'enUS', '', 'Now that you mention it... my mother used to make the best Shrimp Dumplings.$B$BSigh... I miss her...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59047, 'enUS', 'Well you know the only thing that I admire as much as my own looks is a rare white flower that grows around these parts.$B$BIt\'s terribly hard to find, though.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59048, 'enUS', 'You ever tried Wildfowl Roast?$B$BMmm! What would I give for a taste of that right now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59049, 'enUS', '', 'Well I jus\' about like everythin\', but I do like animals the best!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59050, 'enUS', '', 'Being resourceful can often sway a battle in your favor.$b$bI\'ve put together this remedy using plants from the forest around us. Hopefully it will be enough. We need every available fighter in the field.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59051, 'enUS', '', 'You know, I\'ve always like the color of jade. So green, just like the forest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59052, 'enUS', '', 'What a silly question! I\'ll give you a hint. My favorite food has my name in it! Maybe more than once!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59053, 'enUS', '', 'Oh you shouldn\'t! Oh, but I do love gifts, so maybe you should.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59054, 'enUS', '', 'I like anything pretty or shiny.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59055, 'enUS', '', 'Oh you know me. I\'m a humble girl, so I\'d only ask for rubies.$B$BWhat? It\'s not like I\'m asking for diamonds!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59056, 'enUS', '', 'Well, recently I\'ve had quite the craving for Fire Spirit Salmon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59057, 'enUS', 'Well I won\'t lie to you, $n. I\'m one of the most powerful men in the Valley. Most people send me gifts of great value.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59058, 'enUS', 'I\'ll tell you one thing, $n. I like jade, but it\'s not my favorite. Everyone gives it to me, all the time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59059, 'enUS', 'Now that you mention it, I\'ve been craving Charbroiled Tiger Steak for a while! We don\'t get many tigers down in the valley, so the dish is hard to find.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59060, 'enUS', 'No, an old man like me doesn\'t need anything. But my son... now he loved collecting blue feathers...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59061, 'enUS', 'When my son found a blue feather, he used to say that it was a kiss from his mother, up in the sky.$B$B<Old Hillpaw pauses, too choked up to continue.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59062, 'enUS', 'My wife used to make a mean dish of braised turtle. The taste of that dish sure would bring back memories.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59063, 'enUS', '', 'I like things that come from nature.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59064, 'enUS', '', 'A girl in my line of work gets hungry, you know? I wish I had something delicious to eat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59065, 'enUS', '', 'Flowers are nice, but they aren\'t  my favorite.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59066, 'enUS', '', 'I\'m scared of birds! Their feathers can be quite pretty, but they aren\'t my favorite.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59067, 'enUS', '', 'Have you ever tried Eternal Blossom Fish? It\'s my favorite food.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59068, 'enUS', '', 'This is just lovely, thank you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59069, 'enUS', 'See if the new recruits are up to a challenge. They could use a lesson in advanced combat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59070, 'enUS', '', 'Oh, why... thank you... you shouldn\'t have...!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59071, 'enUS', 'Stir fry! Thanks boss!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59072, 'enUS', 'Ooh, Chee Chee is hungry. Thanks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59073, 'enUS', 'Ooh, pretty pretty! So blue... so soft... thanks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59074, 'enUS', 'Very nice, yes yes... a good gift for Chee Chee. Thank you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59075, 'enUS', 'Hey, this is a real nice gift. Thanks, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59076, 'enUS', 'You must be my secret admirer! Well, not so secret anymore. Thanks!', '', 26124);
