INSERT INTO `broadcast_text_locale` VALUES (59089, 'enUS', '', 'Oh, what a breathtaking flower. You know these are supposed to be lucky?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59090, 'enUS', '', 'Oh my, you got me something? Why how can I ever thank you?', 26124);
