INSERT INTO `broadcast_text_locale` VALUES (59092, 'enUS', '', 'This Fire Spirit Salmon looks delicious. I do hope you made it yourself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59093, 'enUS', '', 'Oooh, I can\'t wait to try this Fire Spirit Salmon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59094, 'enUS', '', 'Why you sure know how to impress a lady! Ain\'t no diamond, but I love it anyway!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59095, 'enUS', '', 'This ruby will look wonderful with this dress, don\'t you think?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59096, 'enUS', '', 'How lovely of you to think of me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59097, 'enUS', '', 'You are such a nice $Gguy:gal; $n. Thank you for this lovely present.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59098, 'enUS', 'Oh yes, this ruby looks quite valuable. Thanks, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59099, 'enUS', 'It\'s been too long since I last tasted Charbroiled Tiger Steak.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59100, 'enUS', 'I could eat twenty plates of Charbroiled Tiger Steak and still be looking for more. Thanks, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59101, 'enUS', 'Well, isn\'t this the nicest gift I\'ve gotten today!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59102, 'enUS', 'For me? That\'s mighty nice of you, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59103, 'enUS', 'This feather... brings back so many memories. Thank you, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59104, 'enUS', 'It\'s just like the way my wife used to make it. Thanks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59105, 'enUS', 'Just what an hungry old man needs. Thanks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59106, 'enUS', 'Fer me? Thanks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59107, 'enUS', 'That\'s... nice o\' ya. Thanks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59108, 'enUS', '', 'Thanks for the apple, $r.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59109, 'enUS', '', 'Hey, just what I needed. Thanks a lot.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59110, 'enUS', '', 'Been a while since I had a taste of this dish. This\'ll hit the spot.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59111, 'enUS', '', 'Feels good to look forward to a hot meal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59112, 'enUS', '', 'Huh. I suppose now I have to say thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59113, 'enUS', '', 'Focus on the fires. No - wait - get the saurok! Just ... do something!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59114, 'enUS', '', 'I\'m not leaving until those brewkegs finish their work.', 26124);
