INSERT INTO `broadcast_text_locale` VALUES (59116, 'enUS', '', 'Maybe this will help...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59117, 'enUS', '', 'Stay on your toes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59118, 'enUS', '', 'I\'ve got you...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59119, 'enUS', '', 'Don\'t you die on me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59120, 'enUS', 'Together we can reclaim the temple and restore hope for the people here. It\'s my honor to accompany you in this task.', '', 26124);
