INSERT INTO `broadcast_text_locale` VALUES (59140, 'enUS', 'Sure thing, $n! Enjoy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59141, 'enUS', 'Certainly. If you brought me some meat - which you have not - then I would be able to grill it for you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59142, 'enUS', 'I\'ve got all this fruit here. Could you brew up some hooch for me?', 'I\'ve got all this fruit here. Could you brew up some hooch for me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59143, 'enUS', 'What ingredients do you need to make Apple-Berry hooch?', 'What ingredients do you need to make Apple-Berry hooch?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59144, 'enUS', 'Any time, neighbor. Any time.', '', 26124);
