INSERT INTO `broadcast_text_locale` VALUES (59298, 'enUS', 'Come to me, serpent!  Together we will destroy these intruders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59299, 'enUS', 'The Azure Serpent moves in to attack!', '', 26124);
