INSERT INTO `broadcast_text_locale` VALUES (59301, 'enUS', 'Ahh! Help! What is that thing!? It doesn\'t matter, nothing will stop me now, not when I\'m so close! Maybe if I don\'t look at it...?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59302, 'enUS', 'Yes yes yes! So close! Just a pinch of...oops...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59303, 'enUS', 'Too bitter! Just a drop of honey, and a cane of sugar, and maybe some corn? Yes, more corn!', '', 26124);
