INSERT INTO `broadcast_text_locale` VALUES (59306, 'enUS', 'Such harmony of flavor, such heady aroma! It is...the perfect brew!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59307, 'enUS', 'The name of Stormstout will be sung once again throughout the hills!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59308, 'enUS', 'You again! Don\'t you see? I have made a name for myself at last: I have brewed perfection!', '', 26124);
