INSERT INTO `broadcast_text_locale` VALUES (59332, 'enUS', 'I show you on your map where grummles are now. Find them or wait here for next wave of grummles.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59333, 'enUS', 'My seeds grow the best veggies in the valley. Guaranteed!', '', 26124);
