INSERT INTO `broadcast_text_locale` VALUES (59335, 'enUS', 'Yoon, eh? Never heard of him...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59336, 'enUS', 'He just inherited Sunsong Ranch. You can literally see him from where you are standing.', 'He just inherited Sunsong Ranch. You can literally see him from where you are standing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59337, 'enUS', 'Ohh, of course! The Sunsong place, just over yonder. Was wondering who might be movin\' in on that.$B$B<Greenfield squints over your shoulder and waves at Yoon.>$B$BWhen you come back to market with the biggest, juiciest vegetables anyone\'s ever seen, you tell \'em where you got the seeds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59338, 'enUS', 'Such harmony of flavor, such heady aroma! It is...the perfect brew!', '', 26124);
