INSERT INTO `broadcast_text_locale` VALUES (59347, 'enUS', 'Awaken from ya slumber, my mogu friends. Together, we crush these lands.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59348, 'enUS', 'Destroy those who seek to stop us from retakin\' these lands.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59349, 'enUS', 'You?! I hear all \'bout you. My juju be strong. You not gonna stop the Zandalari!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59350, 'enUS', '', 'It\'s all or nothing, $n. Thunderpaw Refuge needs us. Are you ready to finish this thing?', 26124);
