INSERT INTO `broadcast_text_locale` VALUES (59388, 'enUS', 'Thank you for rescue! Meet me at Burlap Waystation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59389, 'enUS', '', 'It might be wisest if you and your companions were not seen here for a time, traveler.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59390, 'enUS', 'I am ready to leave.', 'I am ready to leave.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59391, 'enUS', 'Report\'s all ready to go, $n! I don\'t mean to brag, but General Nazgrim should be pretty pleased with my work.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59392, 'enUS', '', 'Here\'s the report, $n. Oh! Make sure you mention my idea with the bombs. I have it lined out in the report.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59393, 'enUS', 'This is absolutely barbaric!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59394, 'enUS', 'Bringing our war to these natives is wrong. We should be teaching them the best of ourselves not the worst...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59395, 'enUS', 'There is still time to stop this madness. You don\'t have to do this Shokia...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59396, 'enUS', 'There is so much we can learn from this land, and yet you are content to watch it burn around you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59397, 'enUS', '', 'Report?! I have my hands full down here, $n! Oh alright, here you go.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59398, 'enUS', 'Nazgrim gonna be real happy with Riko! Here! Report is done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59399, 'enUS', '%s calls out for reinforcements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59400, 'enUS', 'Use the Boomer Brew to zap saurok minions!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59401, 'enUS', 'While one player is zapping a saurok, another can instantly destroy it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59402, 'enUS', '', 'You did it! Thunderpaw Refuge is saved!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59403, 'enUS', '', 'And I\'ve got just the brew to celebrate with. First round is on me. Thank you, friends!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59404, 'enUS', 'Come forward, so that we may be properly introduced.', '', 26124);
