INSERT INTO `broadcast_text_locale` VALUES (59815, 'enUS', '', 'Welcome friend!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59816, 'enUS', '', 'Have a seat at the bar, friend. I\'ll get you some food.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59817, 'enUS', '', 'Hello $c!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59818, 'enUS', '', 'Oh, Hello! Can I interest you in a piping hot cup of tea before you leave?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59819, 'enUS', '', 'My grandmother used to tell me it was bad for a fighter\'s balance to enter battle on an empty stomach!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59820, 'enUS', '', 'Hello, $n! You\'re making quite a name for yourself here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59822, 'enUS', '', 'Welcome back, $n! I see you found our missing guides!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59823, 'enUS', '', 'Welcome Back, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59824, 'enUS', '', 'Now that Ji-Lu has his trinkets back maybe I\'ll get some peace in the inn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59825, 'enUS', '', 'Hello again, $c!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59826, 'enUS', '', 'What a pleasure it is to have the great $n in my establishment!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59827, 'enUS', '', 'Greetings, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59828, 'enUS', '', 'You are always welcome at the inn, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59829, 'enUS', '', 'The hero of One Keg returns!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59830, 'enUS', '', 'Ok you two, play nice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59831, 'enUS', 'Welcome to my humble shop, $c.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59832, 'enUS', 'Feel free to browse my wares.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59833, 'enUS', 'Greetings, $c! you look like you might be interested in some fresh spices! Perhaps some books for your journey.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59834, 'enUS', 'Welcome! You won\'t find a better selection east of the camp!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59835, 'enUS', 'Be sure to stop by and see me again, $c!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59836, 'enUS', 'The Vale is my home. It is my duty and my desire to protect it from harm.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59837, 'enUS', 'The winds are good today.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59838, 'enUS', 'Fortune smiles on grummle mounts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59839, 'enUS', 'Come back soon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59840, 'enUS', 'The mountain will forgive the bird, but not always the rider.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59841, 'enUS', 'You here to fish?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59842, 'enUS', 'I can\'t imagine what caused these crocolisks to start attacking. What changed?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59843, 'enUS', 'Thanks again for all your help, $n. The crocolisks aren\'t attacking nearly as much as they were before.$b$bWe\'re also doing our part here. We haven\'t caught any more fish than we need to eat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59844, 'enUS', '', 'Hello, $r.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59845, 'enUS', 'Nothin\' to see here... move along!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59846, 'enUS', 'I\'m a little basket filled with grain...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59847, 'enUS', 'I won\'t go back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59848, 'enUS', 'I\'m just a little grummle in a scary world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59849, 'enUS', 'Are you sure you don\'t want to come with me?', 'Are you sure you don\'t want to come with me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59850, 'enUS', 'Nope! Grummle is safe here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59851, 'enUS', '', 'Allow me to make your underwater experience a little more... pleasant.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59852, 'enUS', 'Can you increase my swim speed again?', 'Can you increase my swim speed again?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59853, 'enUS', 'Aaaaaaaah!', '', 26124);
