INSERT INTO `broadcast_text_locale` VALUES (59855, 'enUS', 'Poot Poot is loose! Run!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (59856, 'enUS', 'Mad yeti! Mad yeti!', '', 26124);
