INSERT INTO `broadcast_text_locale` VALUES (60039, 'enUS', '', 'Still clinging to the tailfeathers of Chi-Ji, and his idiotic notions of \"hope\"?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (60040, 'enUS', '', 'I will show you the meaning of despair... and I will drag this whole island down with me!', 26124);
