INSERT INTO `broadcast_text_locale` VALUES (60045, 'enUS', 'Hold on! This is gonna be a bumpy ride!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (60046, 'enUS', '', 'And so our path lays before us. Speak to Master Shang Xi, he will tell you what comes next.', 26124);
