INSERT INTO `broadcast_text_locale` VALUES (61268, 'enUS', 'Your guess is as good as mine. We have to be ready for anything.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (61269, 'enUS', 'Enough! I\'ll handle you myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (61270, 'enUS', 'Thank you, $p. You fight well for one not of our order. I can handle the rest from here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (61271, 'enUS', 'Return to the Lair of the Jade Witch.', '', 26124);
