INSERT INTO `broadcast_text_locale` VALUES (62257, 'enUS', 'Woooo, $n!', 'Woooo, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62258, 'enUS', 'Welcome to the party, $n!', 'Welcome to the party, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62259, 'enUS', 'This is going to be the best Brewmoon yet with $n here!', 'This is going to be the best Brewmoon yet with $n here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62260, 'enUS', 'I was wondering when you would get here, $n!', 'I was wondering when you would get here, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62261, 'enUS', 'Hey, $n!', 'Hey, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62262, 'enUS', 'It\'s been awhile, $n.', 'It\'s been awhile, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62263, 'enUS', 'Hey, it\'s $n!', 'Hey, it\'s $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62264, 'enUS', 'Have a brew with us, $n!', 'Have a brew with us, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62265, 'enUS', 'Good to see you, $n! Do you have a minute?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62266, 'enUS', 'I\'m so glad you\'re here, $n! Tonight is the night of the Brewmoon Festival and none of the ingredients for my famous Kun-Lai Kicker have been delivered. I sent my assistants out to gather them but none of them have returned. Could you find out what is keeping them?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62267, 'enUS', 'Where can I find Assistant Kieu?', 'Where can I find Assistant Kieu?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62268, 'enUS', 'On the night of the Brewmoon, water spirits can be found on Inkgill Mere performing a ritual. Over the years we have made offerings to them in return for a vial of pure water used in crafting the Kun-Lai Kicker. I sent Assistant Tart out to the lake but she hasn\'t come back yet. Can you head to the north and find out what\'s keeping her, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62269, 'enUS', '', 'YAUNGOL!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62270, 'enUS', 'Where can I find Assistant Tart?', 'Where can I find Assistant Tart?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62271, 'enUS', '', 'Come on, Poppers. Let\'s go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62272, 'enUS', '', 'Good work, $p! Now I can collect the pure water!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62273, 'enUS', 'Kieu, Tart, Buzaki toss the ingredients in!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62274, 'enUS', '', '$n! Brewmast Boof sent me to fetch a vial of pure water from the water spirits but Poppers ate the offering. Now I have no way to convince them to give me one and they don\'t look very friendly. Do you think you could persuade them for me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62275, 'enUS', 'I sent Assistant Kieu to the silos to collect a sack of grain but she still hasn\'t come back. Could you go check on her for me? You can find the silos on the western edge of town.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62276, 'enUS', 'Welcome to Kota Peak. Home of super famous Kota Kafa. A luckydo would be wise to sniff and savor exotic brews.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62277, 'enUS', 'Welcome to our most humble camp.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62278, 'enUS', 'What is your Luckydo?', '', 26124);
