INSERT INTO `broadcast_text_locale` VALUES (62293, 'enUS', 'How do you like the portal? We couldn\'t have done it without your help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62294, 'enUS', 'You may unlearn all of your current Talents for a price, so that you may select new ones.\n\nThis will become more expensive each additonal time you do so.', 'You may unlearn all of your current Talents for a price, so that you may select new ones.\n\nThis will become more expensive each additonal time you do so.', 26124);
