INSERT INTO `broadcast_text_locale` VALUES (62305, 'enUS', 'At level 10 you will choose a Class Specialization.\n\nIf you later decide to choose a different Class Specialization, you may reset your previous choice here for a price.\n\nThis will become more expensive each additonal time you do so.', 'At level 10 you will choose a Class Specialization.\n\nIf you later decide to choose a different Class Specialization, you may reset your previous choice here for a price.\n\nThis will become more expensive each additonal time you do so.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62306, 'enUS', 'Shall we meet in the Challenger\'s Ring?', 'Shall we meet in the Challenger\'s Ring?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62307, 'enUS', 'At level 15 you will choose your first Talent.\n\nIf you later decide to choose different Talents, you may reset your previous choices here for a price.\n\nThis will become more expensive each additonal time you do so.', 'At level 15 you will choose your first Talent.\n\nIf you later decide to choose different Talents, you may reset your previous choices here for a price.\n\nThis will become more expensive each additonal time you do so.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62308, 'enUS', 'At level 25 you will begin to use Glyphs.\n\nIf you later decide to replace your chosen Glyphs, you may reset them here for a price.\n\nThis will become more expensive each additonal time you do so.', 'At level 25 you will begin to use Glyphs.\n\nIf you later decide to replace your chosen Glyphs, you may reset them here for a price.\n\nThis will become more expensive each additonal time you do so.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62309, 'enUS', 'At level 30 you can learn Dual Specialization, which allows you to keep two active Class Specialization, Talent, and Glyph sets and easily switch between them.\n\nYou also will have separate glyph panes and action bars for each set.\n\nSwitching between the two sets cannot be done while in combat and will consume your available resources.', 'At level 30 you can learn Dual Specialization, which allows you to keep two active Class Specialization, Talent, and Glyph sets and easily switch between them.\n\nYou also will have separate glyph panes and action bars for each set.\n\nSwitching between the two sets cannot be done while in combat and will consume your available resources.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62310, 'enUS', 'I have another question.', 'I have another question.', 26124);
