INSERT INTO `broadcast_text_locale` VALUES (62320, 'enUS', '', 'I have waited a long time for this day. Let us face one another in the Challenger\'s Ring.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62321, 'enUS', 'Are you ready?', 'Are you ready?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62322, 'enUS', 'Let us both do our best.', 'Let us both do our best.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62323, 'enUS', 'You fought excellently, just as I knew you could.', 'You fought excellently, just as I knew you could.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62324, 'enUS', 'You have grown much since we first met.', 'You have grown much since we first met.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62325, 'enUS', 'It would be my honor to join you in battle. Speak to me on the hill if you have need of me.', 'It would be my honor to join you in battle. Speak to me on the hill if you have need of me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62326, 'enUS', '[PH] How would you train a Fury Warrior?', '[PH] How would you train a Fury Warrior?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62328, 'enUS', 'I will rip apart every last slave in this valley. Your little uprising will be the death of you all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62329, 'enUS', 'Thank you, friend.', 'Thank you, friend.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62330, 'enUS', 'We didn\'t see it coming...', 'We didn\'t see it coming...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62331, 'enUS', 'I owe you my life.', 'I owe you my life.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62332, 'enUS', 'You have my thanks.', 'You have my thanks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62333, 'enUS', 'I am eternally grateful.', 'I am eternally grateful.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62334, 'enUS', 'I\'m going to make a break for safety. Thank you.', 'I\'m going to make a break for safety. Thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62335, 'enUS', 'I... thank you.', 'I... thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62336, 'enUS', 'I will see you back at the pagoda. Thank you so much.', 'I will see you back at the pagoda. Thank you so much.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62337, 'enUS', 'What... where am I?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62338, 'enUS', 'What has happened here? The land has changed...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62339, 'enUS', 'How long have I been asleep?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (62340, 'enUS', '|TInterface\\Icons\\ability_shaman_fortifyingwaters.blp:20|t%s is about to be healed by |cFFF00000|Hspell:117283|h[Cleansing Waters]|h|r! Move him!', '|TInterface\\Icons\\ability_shaman_fortifyingwaters.blp:20|t%s is about to be healed by |cFFF00000|Hspell:117283|h[Cleansing Waters]|h|r! Move him!', 26124);
