INSERT INTO `broadcast_text_locale` VALUES (63540, 'enUS', 'Friend, this is Dawn\'s Blossom! The heart of Pandaren life here in the Jade Forest. If you wish to make an impression, I am certain many of these citizens could use your help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63541, 'enUS', 'For now I must leave you. Toya is gathering jade for my project. When he is finished I would like you to bring it to me at the Emperor\'s Omen so that we might look at what secrets history has hidden from us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63542, 'enUS', 'Your timing is flawless. I was just about to leave. But please, stay and enjoy the town while Toya gathers the jade we need.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63543, 'enUS', 'See you later!', 'See you later!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63544, 'enUS', 'Bye!', 'Bye!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63545, 'enUS', 'I\'m gonna go play.', 'I\'m gonna go play.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63546, 'enUS', 'Careful with the scorpion, he may look friendly but he is a beast in battle!', 'Careful with the scorpion, he may look friendly but he is a beast in battle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63547, 'enUS', 'Are you eyeing my cat?', 'Are you eyeing my cat?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63548, 'enUS', 'Hello fellow angler!  Have you been around these parts long?  Me and my buddy \"Big Hook\" here travel far and wide looking for the next big catch!$b$bWe ran into this place by accident and the locals were friendly with both their supplies and booze so we decided to stick around!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63549, 'enUS', 'Been working for the anglers?  Check out these rewards!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63550, 'enUS', 'Mogu\'Shan Palace, a level 87-89 dungeon in the Vale of Eternal Blossoms, is now available for testing. Please be in your full group before teleporting to the dungeon. Meeting Stones are not available for this dungeon yet.\n\nTo leave Mogu\'Shan Palace, simply exit through the normal exit inside the dungeon and you will be teleported to Dalaran. If you die and release, you should teleport back from this NPC.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63551, 'enUS', '%s activates his |cFFFF0404|Hspell:119311|h[Stream of Blades]|h|r trap!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63552, 'enUS', 'I\'m in my group and ready to teleport to Mogu\'Shan Palace!', 'I\'m in my group and ready to teleport to Mogu\'Shan Palace!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (63553, 'enUS', 'Take me back to Mogu\'Shan Palace!', 'Take me back to Mogu\'Shan Palace!', 26124);
